INSERT INTO `wp_comments` VALUES (102, 659, 'Chris Keller', '', '', '71.90.100.238', '2011-07-15 15:45:10', '2011-07-15 13:45:10', 'Solid thought here: "The problem is that people aren’t very curious about what’s going on in the world. They’re curious about what’s going on in their world, how something affects them."', 0, '1', 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_6_8) AppleWebKit/534.30 (KHTML, like Gecko) Chrome/12.0.742.112 Safari/534.30', '', 0, 0);
