INSERT INTO `wp_options` VALUES (17364, 0, 'sopablackout_options', 'a:13:{s:9:"test_mode";b:0;s:32:"show_blackout_to_logged_in_users";b:1;s:7:"message";s:969:"On the Tuesday 24th January 2012, the US Senate will vote on the <a href=\\"http://en.wikipedia.org/wiki/Stop_Online_Piracy_Act\\" target=\\"_blank\\">internet censorship bill</a>.\r\n\r\nWhilst it is an American law, it has far reaching repurcusions for the web as a whole.\r\n\r\nThere are many companies against SOPA, such as <a href=\\"http://www.mattcutts.com/blog/internet-censorship-sopa/\\" target=\\"_blank\\">Google</a>, <a href=\\"http://blog.reddit.com/2012/01/stopped-they-must-be-on-this-all.html\\" target=\\"_blank\\">Reddit</a>, <a href=\\"http://news.cnet.com/8301-31921_3-57342914-281/silicon-valley-execs-blast-sopa-in-open-letter/\\" target=\\"_blank\\">Facebook, Twitter, Wikipedia</a>, <a href=\\"http://mozilla.org\\">Mozilla</a> and today I am lending my weight to the argument by taking my site down for the day.\r\n\r\nIf you think SOPA doesn\\''t affect you, please think again. Watch the video below, or use the form below to force politicians to take notice.\r\n\r\nThank you";s:17:"blackoutdate_year";s:4:"2012";s:18:"blackoutdate_month";s:2:"01";s:16:"blackoutdate_day";s:2:"18";s:12:"blackoutdate";s:10:"2012-01-18";s:17:"blackouttimestart";s:1:"8";s:15:"blackouttimeend";s:2:"20";s:16:"blackouttimezone";s:0:"";s:13:"include_video";b:1;s:12:"include_form";b:1;s:10:"page_title";s:33:"Supporting anti-SOPA Blackout day";}', 'yes');
